import { frameworks, url, initSurvey, getSurveyResult, getVisibleListItemByText, test, expect, getButtonByText } from "../helper";
import type { Page } from "@playwright/test";

const title = "allTypes";

const img_base64 =
  "data:image/png;base64,";

var json = {
  elements: [
    {
      type: "text",
      name: "text_question",
    },
    {
      type: "checkbox",
      name: "checkbox_question",
      choices: ["item1"],
    },
    {
      type: "radiogroup",
      name: "radiogroup_question",
      choices: ["item1"],
    },
    {
      type: "dropdown",
      name: "dropdown_question",
      choices: ["item1"],
    },
    {
      type: "comment",
      name: "comment_question",
    },
    {
      type: "rating",
      name: "rating_question",
    },
    {
      type: "imagepicker",
      name: "imagepicker_question",
      choices: [
        {
          value: "item1",
          imageLink: img_base64,
        },
      ],
    },
    {
      type: "boolean",
      name: "boolean_question",
    },
    {
      type: "image",
      name: "image_question",
      imageLink: img_base64,
    },
    {
      type: "html",
      name: "html_question",
      html: "<div class='sjs-html-question'>html text</div>",
    },
    {
      type: "signaturepad",
      name: "signature_question",
      defaultValue: img_base64,
    },
    {
      type: "expression",
      name: "expression_question",
      expression: "1",
    },
    {
      type: "file",
      name: "file_question",
      defaultValue: [
        {
          name:
              "data:image/png;base64,",
          type: "image/png",
          content: img_base64,
        },
      ],
    },
    {
      type: "matrix",
      name: "matrix_question",
      columns: ["Column 1"],
      rows: ["Row 1"],
    },
    {
      type: "matrixdropdown",
      name: "matrixdropdown_question",
      columns: [
        {
          name: "Column 1",
        },
      ],
      choices: [1],
      rows: ["Row 1"],
    },
    {
      type: "matrixdynamic",
      name: "matrixdynamic_question",
      columns: [
        {
          name: "Column 1",
        },
      ],
      choices: [1],
      rowCount: 1,
    },
    {
      type: "multipletext",
      name: "multipletext_question",
      items: [
        {
          name: "text1",
        },
      ],
    },
    {
      type: "panel",
      name: "panel",
      elements: [
        {
          type: "text",
          name: "panel_question",
        },
      ],
      title: "panel_title",
      state: "collapsed",
    },
    {
      type: "paneldynamic",
      name: "paneldynamic",
      templateElements: [
        {
          type: "text",
          name: "paneldynamic_question",
        },
      ],
      panelCount: 1,
    },
    {
      type: "ranking",
      name: "ranking_question",
      choices: ["item1", "item2"],
    },
  ],
};

function findQuestion(page: Page, text: string) {
  return page.locator(".sd-question").filter({ has: page.locator("span").filter({ hasText: text }) }).first();
}

frameworks.forEach(framework => {
  test.describe(`${framework} ${title}`, () => {
    test("check survey will all types", async ({ page }) => {
      await page.goto(`${url}${framework}`);
      await initSurvey(page, framework, json);

      await expect(page.locator(".sv-string-editor")).toHaveCount(0);

      await page.getByRole("textbox", { name: "text_question" }).fill("test text");

      await page.getByLabel("checkbox_question").getByText("item1").click();

      await page.getByLabel("radiogroup_question").getByText("item1").click();

      const dropdown_question = findQuestion(page, "dropdown_question");
      await dropdown_question.getByRole("button", { name: "Select" }).click();
      await getVisibleListItemByText(page, "item1").click();

      await findQuestion(page, "comment_question").locator("textarea")
        .fill("test comment");

      await findQuestion(page, "rating_question").locator("label").filter({ hasText: "3" })
        .click();

      await findQuestion(page, "imagepicker_question").locator("img[alt='item1']").locator("xpath=..")
        .click();

      await findQuestion(page, "boolean_question").locator("span").filter({ hasText: "Yes" }).first()
        .click();

      await page.locator("[data-name='image_question']").locator("img").hover();

      await expect(page.locator(".sjs-html-question")).toHaveText("html text");

      await page.getByLabel("signature_question").getByRole("button", { name: "Clear" })
        .click();

      await page.getByLabel("expression_question").getByLabel("1")
        .hover();

      await page.getByLabel("file_question").getByRole("button", { name: "Clear" })
        .click();

      await page.getByRole("radiogroup", { name: "matrix_question" }).locator("label span")
        .click();

      const matrixDropdownRow = findQuestion(page, "matrixdropdown_question")
        .filter({ has: page.locator("span").filter({ hasText: "matrixdropdown_question" }) })
        .locator("tr")
        .filter({ hasText: "Row 1" });
      await matrixDropdownRow.getByRole("button", { name: "Select" })
        .click();
      await getVisibleListItemByText(page, "1").click();

      await findQuestion(page, "matrixdynamic_question").getByTitle("Remove")
        .click();

      await findQuestion(page, "multipletext_question").locator("input")
        .fill("test multiple text");

      await page.locator("span").filter({ hasText: "panel_title" }).click();

      await findQuestion(page, "paneldynamic").getByRole("button", { name: "Remove" })
        .click();

      await getButtonByText(page, "Complete").click();

      const surveyResult = await getSurveyResult(page);
      expect(surveyResult.text_question).toEqual("test text");
      expect(surveyResult.checkbox_question).toEqual(["item1"]);
      expect(surveyResult.radiogroup_question).toEqual("item1");
      expect(surveyResult.dropdown_question).toEqual("item1");
      expect(surveyResult.comment_question).toEqual("test comment");
      expect(surveyResult.rating_question).toEqual(3);
      expect(surveyResult.imagepicker_question).toEqual("item1");
      expect(surveyResult.boolean_question).toEqual(true);
      expect(surveyResult.expression_question).toEqual(1);
      expect(surveyResult.matrix_question).toEqual({ "Row 1": "Column 1" });
      expect(surveyResult.matrixdropdown_question).toEqual({ "Row 1": { "Column 1": 1 } });
      expect(surveyResult.multipletext_question).toEqual({ text1: "test multiple text" });
    });
  });
});

